public class Solution2309 {
    public String greatestLetter(String s) {
        int[][] dictionary = new int[26][2];
        char[] chars = s.toCharArray();
        for (char c : chars) {
            if (c >= 'A' && c <= 'Z') {
                dictionary[c - 'A'][0]++;
            } else {
                dictionary[c - 'a'][1]++;
            }
        }
        for (int i = 25; i >= 0; i--) {
            if (dictionary[i][0] > 0 && dictionary[i][1] > 0) {
                return String.valueOf((char) (i + 'A'));
            }
        }
        return "";
    }

    public static void main(String[] args) {
        Solution2309 solution2309 = new Solution2309();
        System.out.println(solution2309.greatestLetter(""));
    }
}
